import React, { Component } from "react";
import HowManyDevelopers from "../Images/HowManyDevelopers.png";
import Scroll from "react-scroll";
import {
  Link,
  Button,
  Element,
  Events,
  animateScroll as scroll,
  scrollSpy,
  scroller,
} from "react-scroll";

class ToolsRequiredComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showToolsRequired: false,
    };
  }
  render() {
    return (
      <div>
        <h2>How many developers do I need?</h2>
        <div>
          <img
            src={HowManyDevelopers}
            width="1200"
            className="border"
            alt="Product pie chart"
          />
        </div>
        <button
          className="button-primary mt-2"
          onClick={() => {
            this.setState({
              showToolsRequired: true,
            });
            scroller.scrollTo("myScrollToElement", {
              duration: 1500,
              delay: 100,
              smooth: true,
              containerId: "ContainerElementID",
              offset: 50, // Scrolls to element + 50 pixels down the page
            });
          }}
        >
          Tools required!
        </button>
        {this.state.showToolsRequired ? (
          <Element name="myScrollToElement">
            <div>Tools required</div>
          </Element>
        ) : null}
      </div>
    );
  }
}

export default ToolsRequiredComponent;
