import React, { Component } from "react";
import HowManyDevelopers from "../Images/HowManyDevelopers.png";
import {
  Link,
  Button,
  Element,
  Events,
  animateScroll as scroll,
  scrollSpy,
  scroller,
} from "react-scroll";

class ToolsRequiredComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showToolsRequired: false,
    };
  }
  render() {
    return (
      <div>
        <h2>How many developers do I need?</h2>
        <div>
          <img
            src={HowManyDevelopers}
            width="1200"
            className="border"
            alt="Product pie chart"
          />
        </div>
        <button
          className="button-primary mt-2"
          onClick={() => {
            scroller.scrollTo("toolsReqElement", {
              duration: 800,
              delay: 0,
              smooth: "easeInOutQuart",
            });
            this.setState({
              showToolsRequired: true,
            });

          }}
        >
          Tools required!
        </button>
        {this.state.showToolsRequired ? (
          <Element name="toolsReqElement" className="element">
            <div>
              Tools required
              <p>1</p>
              <p>1</p>
              <p>1</p>
              <p>1</p>
              <p>1</p>
              <p>1</p>
              <p>1</p>
              <p>1</p>
              <p>1</p>
            </div>
          </Element>
        ) : null}
      </div>
    );
  }
}

export default ToolsRequiredComponent;
