import React, { Component } from "react";
import HowManyDevelopers from "../Images/HowManyDevelopers.png";

class ToolsRequiredComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showToolsRequired: false,
    };
    this.toolsReqRef = React.createRef();
  }
  render() {
    return (
      <div>
        <h2>How many developers do I need?</h2>
        <div>
          <img
            src={HowManyDevelopers}
            width="1200"
            className="border"
            alt="Product pie chart"
          />
        </div>
        <button
          className="button-primary mt-2"
          onClick={() => {
            this.setState({
              showToolsRequired: true,
            });
            this.toolsReqRef.current.scrollIntoView();
          }}
        >
          Tools required!
        </button>
        {this.state.showToolsRequired ? (
          <div ref={this.toolsReqRef}>Tools required</div>
        ) : null}
      </div>
    );
  }
}

export default ToolsRequiredComponent;
