import React, { Component } from "react";
import HowManyDevelopers from "../Images/HowManyDevelopers.png";
import { Element, animateScroll as scroll } from "react-scroll";

class ToolsRequiredComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showToolsRequired: false,
    };
  }
  render() {
    return (
      <div>
        <h2>How many developers do I need?</h2>
        <div>
          <img
            src={HowManyDevelopers}
            width="1200"
            className="border"
            alt="Product pie chart"
          />
        </div>
        <button
          className="button-primary mt-2"
          onClick={() => {
            scroll.scrollToBottom();
            this.setState({
              showToolsRequired: true,
            });
          }}
        >
          Tools required!
        </button>
        {this.state.showToolsRequired ? (
          <Element id="toolsReqElement" className="element">
            <div>
              <span style="color:blue; font-size:135px;">&#x2B22;</span>

              <span class="hex1" />
            </div>
          </Element>
        ) : null}
      </div>
    );
  }
}

export default ToolsRequiredComponent;
